import React, {useState} from "react";

interface PointFieldProps {
    value: { x: number, y: number };
    setPoint: ({ x, y }: {x: number, y: number}) => void;
}

const PointField: React.FC<PointFieldProps> = ({ value, setPoint }) => {

    const [x, setX] = useState<number>();
    const [y, setY] = useState<number>();

    const handleXChange = (e) => {
        const newX = Number(e.target.value);
        setX(newX);
        setPoint({ x: newX, y });

        console.log(`New point: ${value}, New X: ${x}`);
    }

    const handleYChange = (e) => {
        const newY = Number(e.target.value);
        setY(newY);
        setPoint({ x, y: newY });
        console.log(`New point: ${value}, New Y: ${y}`);
    }

    return (
        <React.Fragment>
            <label>
                x:
                <input type="number" value={x} onChange={handleXChange} />
            </label>
            <label>
                y:
                <input type="number" value={y} onChange={handleYChange} />
            </label>
        </React.Fragment>
    )
}

export default PointField;
