import React, { useState } from "react";

function App2() {
  const [color, setColor] = useState("#FFFFFF");

  return (
    <div className="app">
      <h2>Pick a color!</h2>
      <input
        type="color"
        value={color} // set initial color
        onChange={(event) => {
          setColor(event.target.value); // change color in hook
        }}
      />
      <br></br>
      <input type="text"/>
      <button onClick={(event)}></button>
      <div className="box" style={{ backgroundColor: color }}></div>
      <h1>Color 1: {color}</h1>
    </div>
  );
}

export default App2;
