    //             new Pixel(updatedLines[index].p1.x, updatedLines[index].p1.y)
    //         );
    //         // Update the appropriate coordinate based on point and axis
    //         if (point === 0) {
    //             targetLine.p0 = new Pixel(
    //                 axis === "x" ? value : targetLine.p0.x,
    //                 axis === "y" ? value : targetLine.p0.y
    //             );
    //         } else {
    //             targetLine.p1 = new Pixel(
    //                 axis === "x" ? value : targetLine.p1.x,
    //                 axis === "y" ? value : targetLine.p1.y
    //             );
    //         }
    //         updatedLines[index] = targetLine;
    //         return updatedLines;
    //     })
    // };

    const handleChangeLine = (index: number, point: number, axis: string, value: number): void => {
        const updatedLines = lineArr.map((line, i) => {
            if (i === index) {
                return line.updatePoint(point, axis, value);
            } else {
                return line;
            }
        });
        console.log()
    };

    const handleAddLine = (): void => {
        // initialize new pixel and line objects
        const newLine = new Line(new Pixel(0, 0), new Pixel(0, 0));
        // insert into existing array
        setLineArr([...lineArr, newLine]);
    }

    const printLines = (): void => {
        console.log("printLines:")
        lineArr.map((item, index) => {
            console.log(`${index}: (${item.p0.x}, ${item.p0.y}), (${item.p1.x}, ${item.p1.y})`)
        })
    }

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas
                lineArr={lineArr}
                width={canvasSize.x}
                height={canvasSize.y}
            />
            {lineArr.map((item: Line, index: number) => (
                <div className="line-field-div" key={index}>
                    <h3>Line {index}</h3>
                    <LineField
                        lineIndex={index}
                        line={item}
                        handleChangeLine={handleChangeLine}
                    />
                </div>
            ))}
            <button type="button" onClick={handleAddLine}>
                Add Line
            </button>
            <button onClick={printLines}>Print Lines</button>
            {/* <LineTest /> */}
        </div>
    );
};

export default App;
