import React, { useState } from "react";
import PropTypes from "prop-types";

function FruitModal(props) {
    const [vis, showVis] = useState();
    return (
        <div class="fruitModal-overlay">
            <div class="fruitModal">
                <h1>{props.name}</h1> 
                <p>{props.description}</p>
                <a 
                    class="fruitModal-link" 
                    href={`https://google.com/search?q=${props.name}`}target="_blank"
                >
                    Search on Google
                </a>
                <br></br>
                <button
                >
                    Close
                </button>
            </div>
        </div>
    )
}

FruitModal.propTypes = {
    name: PropTypes.string.isRequired
};

export default FruitModal;
