import React, {useState} from "react";

interface PointFieldProps {
    value: { x: number, y: number };
    setPoint: ({ x, y }: {x: number, y: number}) => void;
}

const PointField: React.FC<PointFieldProps> = ({ value, setPoint }) => {

    const [x, setX] = useState<number>(0);
    const [y, setY] = useState<number>(0);

    const handleXChange = (e) => {
        const newX = Number(e.target.value);
        setX(newX);

        console.log(`New point: ${value}, New X: ${newX}`);
    }

    const handleYChange = (e) => {
        const newY = Number(e.target.value);
        setY(newY);

        console.log(`New point: ${value}, New Y: ${newY}`);
    }

    return (
        <React.Fragment>
            <label>
                x:
                <input type="text" value={x} onChange={handleXChange} />
            </label>
            <label>
                y:
                <input type="text" value={y} onChange={handleYChange} />
            </label>
        </React.Fragment>
    )
}

export default PointField;
