import React, { useState, useEffect } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineIndex: number;
    handleChangeLine: (
        index: number, 
        point: string | number,
        axis: string,  
        value: number
    ) => void;
    canvasWidth: number,
    canvasHeight: number,
}

const LineField: React.FC<LineFieldProps> = ({ lineIndex, handleChangeLine }) => {

    const [point0, setPoint0] = useState<Pixel>(new Pixel(0, 0));
    const [point1, setPoint1] = useState<Pixel>(new Pixel(canvasWidth / 2, canvasHeight / 2));
    const [color, setColor] = useState<string>('#000000');

    const handleColorChange = (e): void => {
        setColor(e.target.value);
    }

    return (
        <form className="LineField">
            <div>
                <label>
                    {/* Initial x */}
                    x0:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleChangeLine(
                            lineIndex, 
                            0,
                            "x",
                            Number(e.target.value)
                        )}
                    />
                </label>
                <label>
                    {/* Initial y */}
                    y0:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleChangeLine(
                            lineIndex, 
                            0,
                            "y",
                            Number(e.target.value)
                        )}
                    />
                </label>
            </div>
            <div>
                <label>
                    {/* Final x */}
                    x1:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleChangeLine(
                            lineIndex, 
                            1,
                            "x",
                            Number(e.target.value)
                        )}
                    />
                </label>
                <label>
                    {/* Final y */}
                    y1:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleChangeLine(
                            lineIndex, 
                            1,
                            "y",
                            Number(e.target.value)
                        )}
                    />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input type="color" value={color} onChange={handleColorChange} />
                </label>
            </div>
        </form>
    );
}

export default LineField;
