import React, {useState} from "react";

interface PointFieldProps {
    value: { x: number, y: number };
    setPoint: ({ x, y }: {x: number, y: number}) => void;
    canvasWidth: number;
    canvasHeight: number;
}

const PointField: React.FC<PointFieldProps> = ({ value, setPoint, canvasWidth, canvasHeight }) => {

    const [x, setX] = useState<string | number>(value.x === 0 ? '' : value.x);
    const [y, setY] = useState<string | number>(value.y === 0 ? '' : value.y);

    const minX = canvasWidth * -1 / 2;
    const maxX = canvasWidth / 2;
    const minY = canvasHeight * -1 / 2;
    const maxY = canvasHeight / 2;

    const isNumber = (input: string) : boolean => {
        const numberRegex = /^-?\d+(\.\d+)?$/;
        return numberRegex.test(input);
    } 

    const isEmpty = (input: string) : boolean => {
        return input == '';
    }

    const handleXChange = (e): void => {
        if (!isNumber(e.target.value)) {
            console.log("Input a valid number");
            return;
        } else if (Number(e.target.value) < minX || Number(e.target.value) > maxX) {
            console.log(`Input a number between ${minX} and ${maxX}`);
            return;
        }
    }

    const handleYChange = (e): void => {
        if (!isNumber(e.target.value)) {
            console.log("invalid");
            return;
        } else if (Number(e.target.value) < minY || Number(e.target.value) > maxY) {
            console.log(`Input a number between ${minY} and ${maxY}`);
            return;
        }
    }

    console.log(x);
    console.log(y);

    return (
        <React.Fragment>
            <label>
                x:
                <input 
                    type="text" 
                    value={`${x}`} 
                    onChange={handleXChange} 
                    min={`${canvasWidth * -1 / 2}`}
                    max={`${canvasWidth / 2}`}
                />
            </label>
            <label>
                y:
                <input 
                    type="text" 
                    value={`${y}`} 
                    onChange={handleYChange} 
                    min={`${canvasHeight * -1 / 2}`}
                    max={`${canvasHeight / 2}`}
                />
            </label>
        </React.Fragment>
    )
}

export default PointField;
