import React, { useState } from 'react';

import PointField from "./PointField.tsx";

interface LineFieldProps {
    canvasWidth: number;
    canvasHeight: number;
}

const LineField: React.FC<LineFieldProps> = ({canvasWidth, canvasHeight}) => {

    const [point1, setPoint1] = useState({ x: 0, y: 0 });
    const [point2, setPoint2] = useState({ x: canvasWidth / 2, y: canvasHeight / 2 });
    const [color, setColor] = useState<string>('#000000'); 

    const handleColorChange = (e) => {
        setColor(e.target.value);
    }

    return (
        <div className="LineField">
            <h3>Point 1</h3>
            <PointField 
                value={point1} 
                setPoint={setPoint1} 
                canvasWidth={canvasWidth}
                canvasHeight={canvasHeight}
            />
            <h3>Point 2</h3>
            <PointField 
                value={point2} 
                setPoint={setPoint2} 
                canvasWidth={canvasWidth}
                canvasHeight={canvasHeight}
            />
            <label>
                Color:
                <input type="color" value={color} onChange={handleColorChange}/>
            </label>
        </div>
    );
}

export default LineField;
