import React, { useState } from "react";

import FruitCard from "./FruitCard";

function FruitList(props) {

    const fruitList = props.fruits;

    return (
        <ul className="fruitList">
            {props.fruits.map((fruit) => (
                fruit.name.toLowerCase().includes(props.text.toLowerCase()) && // filter
                <li key={fruit.name}>
                    <FruitCard
                        name={fruit.name}
                        background={fruit.color}
                        description={fruit.description}
                    />
                </li>
            ))}
            {props.fruits.filter((fruit) => {
                fruit
                    .name
                    .toLowerCase()
                    .includes(props.text.toLowerCase())
            })


            }
        </ul>
    );
}

export default FruitList;
