import React, { useState } from "react";

import styles from "./WeatherResults.module.css";

function WeatherResults(props) {

    const { input, loading, results, error } = props;

    if (error) return;

    useEffect(() => {
        const city = input;
    }, [loading, results, error])

    if (loading) {
        return <h2 className={styles.LoadingMessage}>Loading...</h2>;
    } else if (!loading && results) {
        return (
            <ul className={styles.WeatherResults}>
                <li><b>City Name: </b>{city}</li>
                <li><b>Temperature: </b>{results.temperature}</li>
                <li><b>Wind: </b>{results.wind}</li>
                <li><b>Description: </b>{results.description}</li>
                {/* {Object.keys(results.forecast).map((forecastKey) => {
                    <li>
                        <p>Day: {forecastKey.day}</p>
                        <p>Temperature: {forecastKey.temperature}</p>
                        <p>Wind: {forecastKey.wind}</p>
                    </li>
                })} */}
            </ul>
        );
    }
}

export default WeatherResults;
