import React, { useState } from "react";

import { Pixel } from './PIxel.tsx';

interface PointFieldProps {
    value: Pixel;
    setPoint: (p: Pixel) => void;
    canvasWidth: number;
    canvasHeight: number;
}

const PointField: React.FC<PointFieldProps> = ({ value, setPoint, canvasWidth, canvasHeight }) => {

    const [x, setX] = useState(value.x);
    const [y, setY] = useState(value.y);

    const minX = canvasWidth * -1 / 2;
    const maxX = canvasWidth / 2;
    const minY = canvasHeight * -1 / 2;
    const maxY = canvasHeight / 2;

    const isNumber = (input: string) => {
        const numRegex = /^-?(\d+(\.\d*)?|\.\d+)?$/;
        return numRegex.test(input);
    }

    const handleXChange = (e): void => {
        const newX = e.target.value;
        if (isNumber(newX)) {
            // Validate that newX is a valid number
            setY(newX);
            setPoint(new Pixel(x, newY));
        }
    }

    const handleYChange = (e): void => {
        const newY = e.target.value;
        if (isNumber(newY)) {
            // Validate that newY is a valid number
            setY(newY);
            setPoint(new Pixel(x, newY));
        }
    }

    return (
        <React.Fragment>
            <label>
                x:
                <input
                    type="text"
                    value={`${x}`}
                    onChange={handleXChange}
                />
            </label>
            <label>
                y:
                <input
                    type="text"
                    value={`${y}`}
                    onChange={handleYChange}
                />
            </label>
        </React.Fragment>
    )
}

export default PointField;
