import React from 'react';
import Pixel from './Pixel.tsx';

interface CanvasProps {
  width: number;
  height: number;
}

interface CanvasState {
  pixels: { x: number; y: number; color: string }[];
}

class Canvas extends React.Component<CanvasProps, CanvasState> {
  constructor(props: CanvasProps) {
    super(props);
    this.state = {
      pixels: [],
    };
  }

  drawPixel = (x: number, y: number, color: string) => {
    this.setState((prevState) => ({
      pixels: [...prevState.pixels, { x, y, color }],
    }));
  };

  render() {
    const { width, height } = this.props;
    const { pixels } = this.state;

    return (
      <div style={{ display: 'grid', gridTemplateColumns: `repeat(${width}, 20px)` }}>
        {Array.from({ length: width * height }).map((_, index) => {
          const x = index % width;
          const y = Math.floor(index / width);
          const pixel = pixels.find((p) => p.x === x && p.y === y);

          return (
            <Pixel
              key={index}
              x={x}
              y={y}
              color={pixel ? pixel.color : 'white'}
              drawPixel={this.drawPixel}
            />
          );
        })}
      </div>
    );
  }
}

export default Canvas;
