import React, { useState } from 'react';

import Canvas from "./components/Canvas.tsx";
import LineField from "./components/LineField.tsx";
import { Pixel } from "./components/Pixel.tsx";
import { Line } from "./components/Line.tsx";

const App: React.FC = () => {

    const canvasSize = { x: 640, y: 480 };

    const defaultP0 = new Pixel(0, 0);
    const defaultP1 = new Pixel(canvasSize.x / 2, canvasSize.y / 2);

    const [lineArr, setLineArr] = useState<Line[]>([new Line(defaultP0, defaultP1)]);

    const handleLineChange = (index: number, property: string, value: string) => {

    }
    const addLineField = (): void => {
        setLineArr([...lineArr, new Line(defaultP0, defaultP1)]);
    }

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas
                width={canvasSize.x}
                height={canvasSize.y}
            />
            {lineArr.map((item: Line, index: number) => (
                <div>
                    <h3>Line {index}</h3>
                    <LineField
                        lineIndex={index}
                        handleLineChange={handleLineChange}
                        canvasWidth={canvasSize.x}
                        canvasHeight={canvasSize.y}
                    />
                </div>
            ))}
            <button type="button" onClick={addLineField}>
                Add Line
            </button>
        </div>
    );
};

export default App;
