        return <h2>Loading...</h2>;
    } else {
        return (
            <ul>
                <li>Temperature: {results.temperature}</li>
                <li>Wind: {results.wind}</li>
                <li>Description: {results.description}</li>
                {/* {Object.keys(results.forecast).map((forecastKey) => {
