import React from "react";
import PropTypes from "prop-types";

import Email from "./Email.jsx";

function EmailList(props) {

    const { emails, removeEmail } = props;

    if (emails.length === 0) {
        return (
            <h3
                style={{
                    margin: "35px auto 0",
                    textAlign: "center",
                    width: "420px",
                    maxWidth: "100%"
                }}
            >
                Empty :( Use the input and button above to add some emails to the list!
            </h3>
        );
    }

    return (
        <ul>
            { /* indices assigned here */ }
            {emails.map((email, index) => (
                <li key={`${email}-${index}`}>
                    <Email
                        email={email}
                        index={index}
                        removeEmail={removeEmail}
                    />
                </li>
            ))}
        </ul>
    )
}

Email.propTypes = {
    emails: PropTypes.array.isRequired,
    removeEmail: PropTypes.func.isRequired
};

export default EmailList;
