import React, { useEffect, useRef } from 'react';

interface CanvasProps {
    width: number,
    height: number
}
const Canvas: React.FC<CanvasProps> = ({width, height}) => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);

  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    if (!context) return;

    const putPixel = (x: number, y: number, color: number[]) => {
      x = canvas.width / 2 + Math.floor(x);
      y = canvas.height / 2 - Math.floor(y) - 1;

      if (x < 0 || x >= canvas.width || y < 0 || y >= canvas.height) {
        return;
      }

      let offset = 4 * x + canvas.width * 4 * y;
      canvasData.data[offset++] = color[0];
      canvasData.data[offset++] = color[1];
      canvasData.data[offset++] = color[2];
      canvasData.data[offset++] = 255; // Alpha = 255 (full opacity)
    };

    const drawLineBroken = (x0: number, y0: number, x1: number, y1: number, color: number[]) => {
      const a = (y1 - y0) / (x1 - x0);
      let y = y0;

      for (let x = x0; x <= x1; x++) {
        putPixel(x, y, color);
        y += a;
      }
    };

    const canvasData = context.getImageData(0, 0, canvas.width, canvas.height);

    drawLineBroken(-200, -100, 240, 120, [0, 0, 0]);
    drawLineBroken(-50, -200, 60, 240, [0, 0, 0]);

    context.putImageData(canvasData, 0, 0);
  }, []);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={height}
      style={{ border: '1px grey solid' }}
    ></canvas>
  );
};

export default Canvas;
