import React, { useRef, useEffect } from 'react';

interface PixelCanvasProps {
  width: number;
  height: number;
}

const Canvas: React.FC<PixelCanvasProps> = ({ width, height }) => {

  const canvasRef = useRef<HTMLCanvasElement | null>(null);

  useEffect(() => {
    if (canvasRef.current) {
      const context = canvasRef.current.getContext('2d');

      if (context) {
        // Draw a red pixel at coordinates (10, 10)
        context.fillStyle = 'red';
        context.fillRect(10, 10, 1, 1);

        // Draw more pixels as needed
        // context.fillRect(x, y, 1, 1);
      }
    }
  }, []);

  return <canvas style={{border: "1px solid black"}} ref={canvasRef} width={width} height={height} />;
};

export default Canvas;
