import React, { useState } from "react";

function App2() {

  function isValidHexColor(text) {
    const hasHashtag = text.startsWith("#");
    const hasCorrectLength = text.length === 7;
    return hasHashtag && hasCorrectLength;
  }

  const [color1, setColor1] = useState("#FFFFFF");
  const [color2, setColor2] = useState("#000000");
  const [text, setText] = useState("");
  const [errorMsg, setErrorMsg] = useState(false);

  return (
    <div className="app">
      <h2>Pick a color!</h2>
      <input
        type="color"
        value={color1} // set initial color
        onChange={(event) => {
          setColor1(event.target.value); // change color in hook
          setErrorMsg(false);
        }}
      />
      <input
        type="color"
        value={color2} // set initial color
        onChange={(event) => {
          setColor1(event.target.value); // change color in hook
          setErrorMsg(false);
        }}
      />
      <br></br>
      <input 
        type="text"
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      />
      <button onClick={(event) => {
        if (isValidHexColor(text)) {
          setColor1(text);
          setErrorMsg(false);
        } else {
          setErrorMsg(true);
        }
      }}>
        Change Color 1
      </button>
      <button onClick={(event) => {
        if (isValidHexColor(text)) {
          setColor2(text);
          setErrorMsg(false);
        } else {
          setErrorMsg(true);
        }
      }}>
        Change Color 2
      </button>
      <br></br>
      <p>{
        errorMsg && (<p>Invalid HEX value. Try again.</p>)
      }</p>
      <div 
        className="box" 
        style={{ backgroundColor: color1 
        }}>

      </div>
      <h1>Color 1: {color1}</h1>
      <h1>Color 1: {color2}</h1>
    </div>
  );
}

export default App2;
