import React, { useState, useEffect } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineIndex: number;
    handleLineChange: (
        index: number, 
        point: string | number,
        axis: string,  
        value: string | number
    ) => void;
}

const LineField: React.FC<LineFieldProps> = ({ lineIndex, handleLineChange }) => {

    const [color, setColor] = useState<string>('#000000');

    const handleColorChange = (e): void => {
        setColor(e.target.value);
    }

    return (
        <form className="LineField">
            <div>
                <label>
                    {/* Initial x */}
                    x0:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleLineChange(
                            lineIndex, 
                            "x", 
                            0, 
                            Number(e.target.value)
                        )}
                    />
                </label>
                <label>
                    {/* Initial y */}
                    y0:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleLineChange(
                            lineIndex, 
                            "y", 
                            0, 
                            Number(e.target.value)
                        )}
                    />
                </label>
            </div>
            <div>
                <label>
                    {/* Final x */}
                    x1:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleLineChange(
                            lineIndex, 
                            "x", 
                            1, 
                            Number(e.target.value)
                        )}
                    />
                </label>
                <label>
                    {/* Final y */}
                    y1:
                    <input
                        type="text"
                        value=""
                        onChange={(e) => handleLineChange(
                            lineIndex, 
                            "y", 
                            1, 
                            Number(e.target.value)
                        )}
                    />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input type="color" value={color} onChange={handleColorChange} />
                </label>
            </div>
        </form>
    );
}

export default LineField;
