import React, { useState } from 'React';
function ColorPicker() {
    const [color, setColor] = useState("#000000");

    return (
        <input
            type="color"
            value={color}
            onChange={(event) => {
                console.log(`New color value: ${event.target.value}`);
                setColor(event.target.value);
            }}
        />
    )
}

export default ColorPicker;
