import React, { useState, useEffect } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineArr: Line[];
    setLineArr: (newLines: Line[]) => void;
    canvasWidth: number;
    canvasHeight: number;
}

const LineField: React.FC<LineFieldProps> = ({ lineArr, setLineArr, canvasWidth, canvasHeight }) => {

    const [point0, setPoint0] = useState(new Pixel(0, 0));
    const [point1, setPoint1] = useState(new Pixel(canvasWidth / 2, canvasHeight / 2));
    const [color, setColor] = useState<string>('#000000');

    const handleColorChange = (e): void => {
        setColor(e.target.value);
    }

    // p2
    const handleX0Change = (e): void => {
        setPoint0(new Pixel(e.target.value, point0.y));
    }

    const handleY0Change = (e) => {
        setPoint0(new Pixel(point0.x, e.target.value));
    }

    // p1
    const handleX1Change = (e) => {
        setPoint1(new Pixel(e.target.value, point1.y))
    }

    const handleY1Change = (e) => {
        setPoint1(new Pixel(point1.x, e.target.value));
    }

    return (
        <form className="LineField">
            <div>
                <label>
                    x:
                    <input
                        type="text"
                        value={`${point0.x}`}
                        onChange={handleX0Change}
                    />
                </label>
                <label>
                    y:
                    <input
                        type="text"
                        value={`${point0.y}`}
                        onChange={handleY0Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    x:
                    <input
                        type="text"
                        value={`${point1.x}`}
                        onChange={handleX1Change}
                    />
                </label>
                <label>
                    y:
                    <input
                        type="text"
                        value={`${point1.y}`}
                        onChange={handleY1Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input type="color" value={color} onChange={handleColorChange} />
                </label>
            </div>
        </form>
    );
}

export default LineField;
