import React, { useState } from "react";

function WeatherResults(props) {

    const { loading, results } = props;

    if (loading || !results) {
        return <h2>Loading...</h2>;
    } else {
        return (
            <ul>
                {Object.keys(results).map((key, index) => (
                    <li key={index}>
                        <p>Temperature: {results[key].temperature}</p>
                        <p>Wind: {results[key].wind}</p>
                        <p>Description: {results[key].description}</p>
                        <ul>
                            {results[key].forecast.map((forecastItem, forecastIndex) => (
                                <li key={forecastIndex}>
                                    <p>Day {forecastItem.day}</p>
                                    <p>Temperature: {forecastItem.temperature}</p>
                                    <p>Wind: {forecastItem.wind}</p>
                                </li>
                            ))}
                        </ul>
                    </li>
                ))}
            </ul>
        );
    }
}

export default WeatherResults;
