const NAMESPACE = "emails";

export function getEmails() {
    return JSON.parse(localStorage.getItem(NAMESPACE) ||  "[]");
}

export function appendEmail(newEmail) {
    const emails = getEmails();
    localStorage.setItem(NAMESPACE, JSON.stringify([...emails, newEmail]));
}

export function removeEmail(index) {
    const emails = getEmails();
    localStorage.setItem(
        NAMESPACE,
        JSON.stringify([...emails].filter((email, i) => i !== index))
    );
}

export function clearEmails() {
    localStorage.clear();
}
