import { Pixel } from "./Pixel.tsx"
import { Line } from "./Line.tsx"
import React, { useState } from "react";

const LineTest = () => {

    const [lineArr, setLineArr] = useState<Line[]>([
        new Line(
            new Pixel(0, 0), 
            new Pixel(10, 10)
        ),
        new Line(
            new Pixel(0, 0), 
            new Pixel(20, 20)
        ),
    ]);

    const handleChangeLine = (index: number, point: number, axis: string, value: number): void => {
        lineArr.map((line, i) => {
            // return i === index 
            //     ? line.updatePoint(point, axis, value) 
            //     : line
            if (i === index) {
                return line.updatePoint(point, axis, value)
            } else {
                return line
            }
        });
    };

    return (
        <button onClick={() => {          
            handleChangeLine(0, 0, 'x', 5);
            handleChangeLine(0, 0, 'y', 5);

            handleChangeLine(0, 1, 'x', 15);
            handleChangeLine(0, 1, 'y', 15);

            handleChangeLine(1, 0, 'x', 18);
            handleChangeLine(1, 0, 'y', 18);

            handleChangeLine(1, 1, 'x', 25);
            handleChangeLine(1, 1, 'y', 25);
            console.log(lineArr);
        }}>
            Line Test
        </button>
    )
}

export default LineTest;
