import React from "react";

import Canvas from "./components/Canvas.tsx";
import LineField from "./components/LineField.tsx";
import { Pixel } from "./components/PIxel.tsx";
import { Line } from "./components/Line.tsx";

const App: React.FC = () => {

    const canvasSize = {x: 640, y: 480};
    const defaultP0 = new Pixel(0,0);
    const defaultP1 = new Pixel(canvasSize.x / 2, canvasSize.y / 2);
    const lines: Line[] = [new Line(defaultP0, defaultP1)];

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas 
                width={canvasSize.x} 
                height={canvasSize.y} 
            />
            <LineField lines={lines} canvasWidth={canvasSize.x} canvasHeight={canvasSize.y}/>
        </div>
    );
};

export default App;
