import React, { useState } from 'react';

import PointField from "./PointField.tsx";

const LineField: React.FC = ({}) => {

    const [point1, setPoint1] = useState({ x: 0, y: 0 });
    const [point2, setPoint2] = useState({ x: 0, y: 0 });
    const [color, setColor] = useState<string>('#000000'); 

    const handleColorChange = (e) => {
        setColor(e.target.value);
    }

    const handleSubmit = (e) => {
        console.log("submitted line");
    }

    return (
        <div>
            <PointField value={point1} setPoint={setPoint1}/>
            <PointField value={point2} setPoint={setPoint2}/>
            <label>
                Color:
                <input type="color" value={color} onChange={handleColorChange}/>
            </label>
        </div>
    );
}

export default LineField;
