import React, { useState } from "react";

function App2() {

  function isValidHexColor(text) {
    const hasHashtag = text.startsWith("#");
    const hasCorrectLength = text.length === 7;
    return hasHashtag && hasCorrectLength;
  }

  const [color, setColor] = useState("#FFFFFF");
  const [text, setText] = useState("");
  const [errorMsg, setErrorMsg] = useState();

  return (
    <div className="app">
      <h2>Pick a color!</h2>
      <input
        type="color"
        value={color} // set initial color
        onChange={(event) => {
          setColor(event.target.value); // change color in hook
        }}
      />
      <br></br>
      <input 
        type="text"
        value={text}
        onChange={(event) => {
          setText(event.target.value);
          setErrorMsg(false);
        }}
      />
      <button onClick={(event) => {
        if (isValidHexColor(text)) {
          setColor(text);
        } else {
          setErrorMsg(true);
        }
      }}>
        Change Color 1
      </button>
