import React, { useState } from "react";

function FruitModal(props) {
    return (
        <div class="fruitModal">

            <h1>{props.name}</h1> 
            <p>{props.description}</p>
            <button
                onClick={() => alert("close")}
            >
                Close
            </button>
        </div>
    )
}

export default FruitModal;
