import React from "react";

function App() {
  const [count, setCount] = useState(0);

  return (
    <main>
      <h1>Number Counter</h1>
      <h2>My first React app</h2>
      <h3>Counter: {count}</h3>
      <button onClick={ () => setCount(count++) }>Increment</button>
      <button>Reset</button>
    </main>
  );
}

export default App;
