import React, { useState, useEffect } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineIndex: number;
    handleLineChange: (
        index: number, 
        axis: string, 
        point: string | number, 
        value: string | number
    ) => void;
    canvasWidth: number;
    canvasHeight: number;
}

const LineField: React.FC<LineFieldProps> = ({ lineIndex, handleLineChange, canvasWidth, canvasHeight }) => {

    const [color, setColor] = useState<string>('#000000');

    const handleColorChange = (e): void => {
        setColor(e.target.value);
    }

    return (
        <form className="LineField">
            <div>
                <label>
                    x:
                    <input
                        type="text"
                        value={`${point0.x}`}
                        onChange={(e) => handleLineChange(
                            lineIndex, 
                            'x', 
                            e.target.value
                        )}
                    />
                </label>
                <label>
                    y:
                    <input
                        type="text"
                        value={`${point0.y}`}
                        onChange={handleY0Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    x:
                    <input
                        type="text"
                        value={`${point1.x}`}
                        onChange={handleX1Change}
                    />
                </label>
                <label>
                    y:
                    <input
                        type="text"
                        value={`${point1.y}`}
                        onChange={handleY1Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input type="color" value={color} onChange={handleColorChange} />
                </label>
            </div>
        </form>
    );
}

export default LineField;
