import React, { useState, useEffect } from "react";

import Hero from "./components/Hero/Hero.jsx";
import SearchForm from "./components/SearchForm/SearchForm.jsx";
import WeatherResults from "./components/WeatherResults/WeatherResults.jsx";
import ErrorMessage from "./components/ErrorMessage/ErrorMessage.jsx";

function App11() {

  const [currCity, setCurrCity] = useState();
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const getWeatherForecasts = async (city) => {

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`https://goweather.herokuapp.com/weather/${encodeURIComponent(city)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json(); // returns object
      setCurrCity(city);
      setResults(data);
    } catch {
      setError(`Failed to fetch forecast data for ${city}`);
    } finally {
      setLoading(false);
    }
  }

  // useEffect(() => {
  //   getWeatherForecasts(input);
  // }, [])

  return (
    <div>
      <Hero />
      <SearchForm 
        getWeatherForecasts={getWeatherForecasts}
      />
      <WeatherResults 
        currCity={currCity}
        loading={loading}
        results={results}
        error={error}
      />
      <ErrorMessage 
        error={error}
      />
    </div>
  );
}

export default App11;
