import React from "react";

import Canvas from "./components/Canvas.tsx";
import LineField from "./components/LineField.tsx";
import { Pixel } from "./components/PIxel.tsx";
import { Line } from "./components/Line.tsx";

const App: React.FC = () => {

    const canvasSize = {x: 640, y: 480};
    const lines: Line[] = [new Line(new Pixel(0, 0), new Pixel({x: canvasSize.x / 2, y: canvasSize/y / 2}))]

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas width={canvasSize.x} height={canvasSize.y} />
            <LineField canvasWidth={canvasSize.x} canvasHeight={canvasSize.y}/>
        </div>
    );
};

export default App;
