import React, { useState } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineIndex: number;
    lineObj: Line;
    handleChangeLine: (
        index: number,
        point: number,
        axis: string,
        value: number
    ) => void;
}

const LineField: React.FC<LineFieldProps> = ({ lineIndex, lineObj, handleChangeLine }) => {

    const [point0, setPoint0] = useState<Pixel>(new Pixel(lineObj.p0.x, lineObj.p0.y));
    const [point1, setPoint1] = useState<Pixel>(new Pixel(lineObj.p1.x, lineObj.p1.y));
    const [color, setColor] = useState<string>('#000000');

    // initial point
    const handleX0Change = (e): void => {
        const newX = Number(e.target.value);
        setPoint0((prevPoint0) => new Pixel(newX, prevPoint0.y));
        handleChangeLine(lineIndex, 0, "x", newX);
    }

    const handleY0Change = (e): void => {
        const newY = Number(e.target.value);
        setPoint0((prevPoint0) => new Pixel(prevPoint0.x, newY));
        handleChangeLine(lineIndex, 0, "y", newY);
    }

    // final point
    const handleX1Change = (e): void => {
        const newX = Number(e.target.value);
        setPoint1((prevPoint1) => new Pixel(newX, prevPoint1.y));
        handleChangeLine(lineIndex, 1, "x", newX);
    }

    const handleY1Change = (e): void => {
        const newY = Number(e.target.value);
        setPoint1((prevPoint1) => new Pixel(prevPoint1.x, newY));
        handleChangeLine(lineIndex, 1, "y", newY);
    }

    const handleColorChange = (e): void => {
        setColor(e.target.value);
    }

    // console.log(`line ${lineIndex}: (${point0.x}, ${point0.y}), (${point1.x}, ${point1.y})`);

    return (
        <form className="LineField">
            <div>
                <label>
                    {/* Initial x */}
                    x0:
                    <input
                        type="text"
                        value={point0.x}
                        onChange={handleX0Change}
                    />
                </label>
                <label>
                    {/* Initial y */}
                    y0:
                    <input
                        type="text"
                        value={point0.y}
                        onChange={handleY0Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    {/* Final x */}
                    x1:
                    <input
                        type="text"
                        value={point1.x}
                        onChange={handleX1Change}
                    />
                </label>
                <label>
                    {/* Final y */}
                    y1:
                    <input
                        type="text"
                        value={point1.y}
                        onChange={handleY1Change}
                    />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input
                        type="color"
                        value={color}
                        onChange={handleColorChange}
                    />
                </label>
            </div>
        </form>
    );
}

export default LineField;
