import React, { useState } from "react";

function WeatherResults({ loading, results }) {
    if (loading || !results) {
      return <h2>Loading...</h2>;
    } else {
      return (
        <ul>
          {results.map((item, index) => (
            <li key={index}>
              <p>Temperature: {item.temperature}</p>
              <p>Wind: {item.wind}</p>
              <p>Description: {item.description}</p>
              <ul>
                {item.forecast.map((forecastItem, forecastIndex) => (
                  <li key={forecastIndex}>
                    <p>Day {forecastItem.day}</p>
                    <p>Temperature: {forecastItem.temperature}</p>
                    <p>Wind: {forecastItem.wind}</p>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      );
    }
  }

export default WeatherResults;
