import React, { useState } from "react";

import WarningMessage from "./components/WarningMessage";
import ErrorMessage from "./components/ErrorMessage";
import SuccessMessage from "./components/SuccessMessage";
import ColorPicker from "./components/ColorPicker";

function App3() {
  const [text, setText] = useState("");
  const [upperCase, setUpperCase] = useState(false);
  const [color, setColor] = useState("")
  const hasNoChars = text.length == 0;
  const hasNumbers = /\d/.test(text);

  const hasMoreThanTenChars = text.length > 10; // changes on re-render

  const handleChange = (event) => {
    setText(event.target.value);
  };

  return (
    <div>
      <h1 class="title">Character counter</h1>
      {hasNoChars && <WarningMessage />}
      {hasNumbers && <ErrorMessage />}
      {hasMoreThanTenChars && <SuccessMessage />}
      <main className="main">
        <input
          className="textInput"
          placeholder="Enter a word"
          value={text}
          onChange={handleChange} // re-render w/ new text
          style={{
            borderColor: hasMoreThanTenChars ? "#86DC3D" : "black"
          }}
        />
        <p className="word">
          <b>Word:</b> {text}
        </p>
        <p>Number of characters: {text.length}</p>
        <button
          onClick={() => {
            setText("");
          }}
        >
          Reset
        </button>
        <button
          onClick={() => {
            setUpperCase(!upperCase);
            setText(
              upperCase ? text.toUpperCase() : text.toLowerCase()
            )
          }}
        >
          {!upperCase ? "Uppercase" : "Lowercase"}
        </button>
        <ColorPicker 
          value={color}
          onChange={(event) => {
            setColor(event.target.value);
          }}
        />
        <div 
          style={{
            backgroundColor: color,
            width: text.length * 10,
            height: text.length * 10,
            margin: "auto",
            marginTop: "18px",
          }}
        >
        </div>
      </main>
    </div>
  );
}

export default App3;
