import React, { useState } from 'react';

import { Pixel } from './Pixel.tsx';
import { Line } from './Line.tsx';

interface LineFieldProps {
    lineIndex: number;
    lineObj: Line;
    handleChangeLine: (
        index: number,
        point: number,
        axis: string,
        value: number
    ) => void;
}

const LineField: React.FC<LineFieldProps> = ({ lineIndex, lineObj, handleChangeLine }) => {

    const [point0, setPoint0] = useState<Pixel>(new Pixel(lineObj.p0.x, lineObj.p0.y));
    const [point1, setPoint1] = useState<Pixel>(new Pixel(lineObj.p1.x, lineObj.p1.y));
    const [color, setColor] = useState<string>('#000000');

    // initial point
    const handleCoordinateChange = (point: number, axis: string, value: number): void => {
        handleChangeLine(lineIndex, point, axis, value);
    };

    // console.log(`line ${lineIndex}: (${point0.x}, ${point0.y}), (${point1.x}, ${point1.y})`);

    return (
        <form className="LineField">
            <div>
                <label>
                    x0:
                    <input type="text" value={line.p0.x} onChange={(e) => handleCoordinateChange(0, 'x', Number(e.target.value))} />
                </label>
                <label>
                    y0:
                    <input type="text" value={line.p0.y} onChange={(e) => handleCoordinateChange(0, 'y', Number(e.target.value))} />
                </label>
            </div>
            <div>
                <label>
                    x1:
                    <input type="text" value={line.p1.x} onChange={(e) => handleCoordinateChange(1, 'x', Number(e.target.value))} />
                </label>
                <label>
                    y1:
                    <input type="text" value={line.p1.y} onChange={(e) => handleCoordinateChange(1, 'y', Number(e.target.value))} />
                </label>
            </div>
            <div>
                <label>
                    Color:
                    <input
                        type="color"
                        value={color}
                        onChange={handleColorChange}
                    />
                </label>
            </div>
        </form>
    );
}

export default LineField;
