import React, { useState } from 'react';

import Canvas from "./components/Canvas.tsx";
import LineField from "./components/LineField.tsx";
import { Pixel } from "./components/Pixel.tsx";
import { Line } from "./components/Line.tsx";

const App: React.FC = () => {

    const canvasSize = {x: 640, y: 480};

    const defaultP0 = new Pixel(0,0);
    const defaultP1 = new Pixel(canvasSize.x / 2, canvasSize.y / 2);

    const [lines, setLines] = useState<Line[]>([new Line(defaultP0, defaultP1)]);

    const handleAddLine = (newLine: Line): void => {
        setLines([...lines, newLine]);
    }

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas 
                width={canvasSize.x} 
                height={canvasSize.y} 
            />
            <LineField 
                lines={lines} 
                setLines={setLines} 
                canvasWidth={canvasSize.x}
                canvasHeight={canvasSize.y}
            />
            {/* <button type="button" onClick={handleAddLine}>
                Add Line
            </button> */}
        </div>
    );
};

export default App;
