import React, { useState } from "react";

import WarningMessage from "./components/WarningMessage";
import ErrorMessage from "./components/ErrorMessage";

function App3() {
  const [text, setText] = useState("");
  const wordIsEmpty = text.length == 0;
  const wordHasNumbers = (text) => {
    return /\d/.test(text);
  }

  const hasMoreThanTenChars = text.length > 10; // changes on re-render

  const handleChange = (event) => {
    setText(event.target.value);
  };

  return (
    <div>
      <h1 class="title">Character counter</h1>
      {wordIsEmpty && <WarningMessage />}
      {wordHasNumbers && <ErrorMessage />}
      <main className="main">
        <input
          className="textInput"
          placeholder="Enter a word"
          value={text}
          onChange={handleChange} // re-render w/ new text
          style={{
            borderColor: hasMoreThanTenChars ? "#86DC3D" : "black"
          }}
        />
        <p className="word">
          <b>Word:</b> {text}
        </p>
        <p>Number of characters: {text.length}</p>
      </main>
    </div>
  );
}

export default App3;
