import React, { useState, useEffect } from "react";

import AddEmail from "./components/AddEmail";
import EmailList from "./components/EmailList.jsx";
import * as storage from "./utils/storage.jsx";

function App() {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    setEmails(storage.getEmails());
  }, []);

  const addEmail = (newEmail) => {
    storage.appendEmail(newEmail);
    setEmails((prevEmails) => {
      return [...prevEmails, newEmail]; 
    })
  }

  const removeEmail = (index) => {
    storage.removeEmail(index);
    setEmails((prevEmails) => {
      return [...prevEmails].filter((email, i) => i !== index);
    });
  }

  return (
    <div className="App">
      <AddEmail addEmail={addEmail}/>
      <EmailList emails={emails} removeEmail={removeEmail}/>
    </div>
  );
}

export default App;
