import React, { useState } from 'react';

import Canvas from "./components/Canvas.tsx";
import LineField from "./components/LineField.tsx";
import { Pixel } from "./components/Pixel.tsx";
import { Line } from "./components/Line.tsx";

const App: React.FC = () => {

    const canvasSize = { x: 640, y: 480 };

    const [lineArr, setLineArr] = useState<Line[]>(
        [new Line(
            new Pixel(0, 0), 
            new Pixel(0, 0)
        )]
    );

    const handleChangeLine = (
        index: number,
        point: number,
        axis: string,
        value: number
    ): void => {
        setLineArr((prevLineArr) => {
            const updatedLines = [...prevLineArr];
            const targetLine = updatedLines[index];

            if (point === 0) {
                // Update coordinates for point 0
                if (axis === "x") {
                    targetLine.p0.x = value;
                } else {
                    targetLine.p0.y = value;
                }
            } else {
                // Update coordinates for point 1
                if (axis === "x") {
                    targetLine.p1.x = value;
                } else {
                    targetLine.p1.y = value;
                }
            }

            return updatedLines;
        });
    };

    // const handleChangeLine = (
    //     index: number,
    //     point: number,
    //     axis: string,
    //     value: number
    // ): void => {
    //     const targetLine = lineArr[index];
    //     if (point === 0) {
    //         // Update coordinates for point 0
    //         if (axis === "x") { 
    //             targetLine.p0.x = value;
    //         } else {
    //             targetLine.p0.y = value;
    //         }
    //     } else if (point === 1) {
    //         // Update coordinates for point 1
    //         if (axis === "x") {
    //             targetLine.p1.x = value;
    //         } else {
    //             targetLine.p1.y = value;
    //         }
    //     }
    //     console.log(`Line ${index}: (${targetLine.p0.x}, ${targetLine.p0.y}), (${targetLine.p1.x}, ${targetLine.p1.y})`);
    // };


    const handleAddLine = (): void => {
        // initialize new pixel objects
        const newLine = new Line(new Pixel(0, 0), new Pixel(0, 0));
        // initialize new line object and insert into array
        setLineArr([...lineArr, newLine]);
    }

    // console.log("AFTER ADDING");
    // lineArr.map((item, index) => {
    //     console.log(`Line ${index}: (${item.p0.x}, ${item.p0.y}), (${item.p1.x}, ${item.p1.y})`);
    // })

    const printLines = (): void => {
        console.log("PRINTING LINES")
        lineArr.map((item, index) => {
            console.log(`${index}: (${item.p0.x}, ${item.p0.y}), (${item.p1.x}, ${item.p1.y})`)
        })
    }

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Rasterizer in the Web</h1>
            <Canvas
                lineArr={lineArr}
                width={canvasSize.x}
                height={canvasSize.y}
            />
            {lineArr.map((item: Line, index: number) => (
                <div className="line-field-div" key={index}>
                    <h3>Line {index}</h3>
                    <LineField
                        lineIndex={index}
                        lineObj={item}
                        handleChangeLine={handleChangeLine}
                    />
                </div>
            ))}
            <button type="button" onClick={handleAddLine}>
                Add Line
            </button>
            <button onClick={printLines}>Print Lines</button>
        </div>
    );
};

export default App;
